import React, { useContext, useState } from 'react';
import { LikeCount } from '../App';

export default function Card({ name, email, id, image, location }) {

    // const { count, setCount } = useContext(LikeCount);
    const { count, setCount } = useContext(LikeCount);
    const [ cardCount, setCardCount ] = useState(0);
    return (
        <div className="card mb-4 box-shadow">
            <div className="card-header">
                <h4 className="my-0 font-weight-normal">{name} </h4>
            </div>
            <div className="card-body">
                <img src={image} alt={name} />
                <ul className="list-unstyled mt-3 mb-4">
                    <li>{email}</li>
                    <li>{location}</li>
                </ul>
                <button type="button" className="btn btn-lg btn-block btn-outline-primary" onClick={() => {setCardCount(cardCount + 1) ; setCount(count + 1)}}>
                    LIKE {cardCount}
                </button>
            </div>
        </div>
    )
}
